import React from 'react';
import './App.css';
import Navbar from './components/navbar/Navbar';
import Clarifai from 'clarifai';
import Logo from './components/logo/Logo';
import Rank from './components/rank/Rank'
import Particle from './components/Particle/Particle'
import FaceRecogination from './components/FaceRecogination/FaceRecogination'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm'
import SignIn from './components/SignIn/SignIn'
import Register from './components/Register/Register'
const app = new Clarifai.App({
      apiKey: "8821cc9a7a764a11a48e02afbce265fd",
    });

class App extends React.Component{
  constructor(){
    super()
    this.state ={
      input : '',
      imageUrl : '',
      box : {},
      route : 'SignIn',
      isSignedIn : "false"
    }
  }
  calculateFaceLocation = (data) =>{
     const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
     const image = document.getElementById('imageInput');
     const width = Number(image.width);
     const height = Number(image.height);
     return {
     left: clarifaiFace.left_col * width,
     top : clarifaiFace.top_row * height,
     right : width - (clarifaiFace.right_col * width),
     bottom : height -(clarifaiFace.bottom_row * height)
     }
  }

displayFaceBox = (box) => {
   this.setState({box :box});
}

  onSearchChange= (event) =>{
    this.setState({input:event.target.value})
  }

  onButtonClick =() =>{
    this.setState({imageUrl :this.state.input});
    app.models
      .predict(
        Clarifai.FACE_DETECT_MODEL,
        this.state.input)
      .then(response =>
        this.displayFaceBox(this.calculateFaceLocation(response)))
      .catch(err => console.log(err))
  }

  onRouteChange = (route) =>{
    this.setState({route : route});
    if(route === 'signout'){
      this.setState({isSignedIn : false})
    }else if (route === 'home'){
      this.setState({isSignedIn:true})
    }

  }
  render(){
     return (
      <div className="App">

         <Navbar isSignedIn= {this.state.isSignedIn} onRouteChange = {this.onRouteChange}/>
         <Particle/>
         { this.state.route === 'home' ?
         <div>
         <Logo/>
         <Rank/>
         <ImageLinkForm
         onSearchChange = {this.onSearchChange}
         onButtonClick = {this.onButtonClick} />
         <FaceRecogination imageUrl = {this.state.imageUrl}
                           box = {this.state.box}/>
         </div>
         :(this.state.route === 'SignIn'?
         <SignIn onRouteChange = {this.onRouteChange}/>:
         <Register onRouteChange = {this.onRouteChange}/>
         )
          }
      </div>
    );
   }
  }

export default App;
