import React from 'react';
import './App.css';
import Navbar from './components/navbar/Navbar';
import Clarifai from 'clarifai';
import Logo from './components/logo/Logo';
import Rank from './components/rank/Rank'
import FaceRecogination from './components/FaceRecogination/FaceRecogination'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm'
const app = new Clarifai.App({
      apiKey: "8821cc9a7a764a11a48e02afbce265fd",
    });

class App extends React.Component{
  constructor(){
    super()
    this.state ={
      input : '',
      imageUrl : '',
      box : {}
    }
  }
  calculateFaceLocation = (data) =>{
     const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
     const image = document.getElementById('imageInput');
     const width = Number(image.width);
     const height = Number(image.height);
     return {
     leftCol: clarifaiFace.left_col * width,
     topRow : clarifaiFace.top_row * height,
     rightCol : width - (clarifaiFace.right_col * width),
     bottomRow : height -(clarifaiFace.bottom_row * height)
     }
  }

displayFaceBox = (box) => {
  console.log (box)
   this.setState({box :box})
}




  onSearchChange= (event) =>{
    this.setState({input:event.target.value})
  }

  onButtonClick =() =>{
    this.setState({imageUrl :this.state.input});
    app.models
      .predict(
        Clarifai.FACE_DETECT_MODEL,
        // THE JPG
        this.state.input
      )
      .then(response =>
        this.didplayFaceBox(this.calculateFaceLocation(response)))
      .catch(err => console.log(err))

  }
  render(){
     return (
      <div className="App">

         <Navbar/>
         <Logo/>
         <Rank/>
         <ImageLinkForm
         onSearchChange = {this.onSearchChange}
         onButtonClick = {this.onButtonClick} />
         <FaceRecogination imageUrl = {this.state.imageUrl}/>
      </div>
    );
   }
  }

export default App;
